import React, { useState } from 'react';
import "./Reservation.css";

function Reservation() {
    const validateUser=()=>{

    }
  const [formData, setFormData] = useState({
    id: '',
    name: '',
    age: '',
    travelsname:'',
    date:'',
    boardingPoint: '',
    endingPoint: ''

  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(formData); 
  };

  return (
    <form onSubmit={handleSubmit}>
        <center><h1>Bus Reservation üöç</h1></center>
      <label>
        Id:
        <input type="text" name="id" value={formData.id} onChange={handleInputChange} />
      </label>
      <br />
      <label>
        Name:
        <input type="text" name="name" value={formData.name} onChange={handleInputChange} />
      </label>
      <br />
      <label>
        Age:
        <input type="text" name="age" value={formData.age} onChange={handleInputChange} />
      </label>
      <br />
      <label>
        Travelsname:
        {/* <input type="text" name="travelsname" value={formData.travelsname} onChange={handleInputChange} /> */}
        <select name="travelsname" id="travels">
  <option value="travels1">Essaar Travels</option>
  <option value="travels2">Supaa Travels</option>
  <option value="travels3">Sri Kumaran Travels</option>
  <option value="travels4">A1 Travels</option>
  <option value="travels5">Amarnath Travels</option>
  <option value="travels6">Air Indiaa Travels</option>
  <option value="travels7">Krish Travels</option>
  <option value="travels8">Sps Travels</option>
</select>
      </label>
      <br />
      <label>
        Age:
        <input type="date" name="date" value={formData.date} onChange={handleInputChange} />
      </label>
      <br />
      <label>
        Boarding Point:
        <input type="text" name="boardingPoint" value={formData.boardingPoint} onChange={handleInputChange} />
      </label>
      <br />
      <label>
        Ending Point:
        <input type="text" name="endingPoint" value={formData.endingPoint} onChange={handleInputChange} />
      </label>
      <br />
      <button type="submit" onClick={validateUser} >Submit</button>
    </form>
  );
}

export default Reservation;
